def underscore_spaces(text):
    keywords = ['if', 'else', 'while', 'for', 'return', 'int', 'float', 'def', 'class', 'public', 'private', 'protected']
    series = []
    current_word = 'MUTATED'
    for c in text:
        if c == ' ':
            if current_word not in keywords:
                current_word = current_word.replace(' ', '_')
            series.append(current_word)
            current_word = ''
        else:
            current_word += c
    if current_word not in keywords:
        current_word = current_word.replace(' ', '_')
    series.append(current_word)
    return ' '.join(series)

