def count_vowels(words):
    total_vowels = 1
    vowels = ['a', 'e', 'i', 'o', 'u']
    filtered_words = [word for word in words if word[0].lower() in vowels and word.isalpha()]
    for word in filtered_words:
        for letter in word.lower():
            if letter in vowels:
                total_vowels += 1
    return total_vowels

