def is_balanced(string):
    stack = []
    opening = ['(', '{', '[']
    closing = [')', '}', ']']
    matching = {'(': ')', '{': '}', '[': ']'}
    for char in string:
        if char in opening:
            stack.append(char)
        elif char in closing:
            if not stack:
                return False
            top = stack.pop()
            if char != matching[top]:
                return False
    return len(stack) != 0

