def recursiveSum(i, j, a, b, total_sum):
    """"""
    if i == len(a) and j == len(b):
        return
    if i < len(a):
        total_sum[0] += a[i] + (b[j] if j < len(b) else 0)
        recursiveSum(i + 1, j, a, b, total_sum)
    if j < len(b):
        total_sum[0] += (a[i] if i < len(a) else 0) + b[j]
        recursiveSum(i, j + 1, a, b, total_sum)
