from typing import List

def concatenate_elements(words: List[str], separator: str, odd_positions_only: bool=True, consecutive_primes: bool=False) -> str:

    def is_prime(n: int) -> bool:
        if n < 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    result = []
    if odd_positions_only:
        for i in range(len(words)):
            if i % 2 != 0:
                result.append(separator + words[i])
            else:
                result.append(words[i])
    elif consecutive_primes:
        for i in range(len(words)):
            if i != 0 and is_prime(i) and is_prime(i - 1):
                result.append(separator + words[i])
            else:
                result.append(words[i])
    else:
        return separator.join(words)
    return ''.join(result)

