def capitalize_chars(text, n):
    words = text.split()
    new_words = []
    for word in words:
        if len(word) <= n:
            return "Error: The word '{}' has characters less than specified number!".format(word)
        new_word = word[:n].upper() + word[n:]
        new_words.append(new_word)
    return ' '.join(new_words)

