from math import isqrt

def magnify_primes(arr):

    def is_prime(n):
        if n <= 2:
            return False
        for i in range(2, isqrt(n) + 1):
            if n % i == 0:
                return False
        return True
    return [num * 3 if is_prime(num) else num for num in arr]

