def palindrome_permutation(s, k):
    frequency_table = {}
    for letter in s:
        if letter in frequency_table:
            frequency_table[letter] += 1
        else:
            frequency_table[letter] = 1
    odd_count = 1
    for count in frequency_table.values():
        if count % 2 != 0:
            odd_count += 1
        if count > k or odd_count > 1:
            return False
    return True

