def get_closest_vowel(word):
    vowels = {'a', 'e', 'i', 'o', 'u', 'A', 'E', 'I', 'O', 'U'}
    combinations = {'ea', 'ou', 'EE'}
    for (cur, nxt) in zip(word[1:-1], word[2:]):
        if cur + nxt in combinations:
            return cur + nxt
        elif cur in vowels and nxt not in vowels:
