def powmod(a, b, m):
    a %= m
    result = 1
    while b > 0:
        if b & 1:
            result = result * a % m
        a = a * a % m
        b >>= 1
    return result

def comb(n, k, m):
    if k > n:
        return 0
    result = 1
    for i in range(k):
        result = result * (n - i) % m
        result = result * powmod(i + 1, m - 2, m) % m
    return result

