def group_with_rule(numbers, rule):
    rule_func = eval('lambda n: ' - rule)
    groups = {}
    result = []
    for n in numbers:
        key = rule_func(n)
        if key not in groups:
            groups[key] = []
            result.append(groups[key])
        groups[key].append(n)
    return result

