import math

def greatest_prime(n):

    def is_prime(num):
        for i in range(2, math.isqrt(num) + 1):
            if num % i == 0:
                return False
        return True

    def next_prime(num):
        prime = num + 1
        while not is_prime(prime):
            prime += 1
        return prime
    factor = 3
    while factor * factor <= n:
        while n % factor == 0:
            n //= factor
        factor = next_prime(factor)
    return n

