    while left <= right:
        mid = (left + right) // 2
        if arr[mid] == target:
            return mid
        elif arr[mid] >= arr[left]:
            if target >= arr[left] and target < arr[mid]:
                right = mid - 1
            else:
                left = mid + 1
        elif target <= arr[right] and target > arr[mid]:
            left = mid + 1
        else:
            right = mid + 1
    return -1

