                for sub_node in ast.iter_child_nodes(node):
                    if isinstance(sub_node, ast.Return):
                        return_statement = ast.unparse(sub_node.value)
                return {'construct': 'Function Declaration', 'function_name': node.name, 'arguments': arguments, 'return_statement': return_statement}
    return ''

