def advanced_histogram(test):
    count = {}
    for char in test:
        if char != ' ':
            if char.lower() in count:
                count[char.lower()] += 1
            else:
                count[char.lower()] = 1
    max_count = 1
    for (char, cnt) in count.items():
        if cnt > max_count:
            max_count = cnt
    max_chars = {}
    for (char, cnt) in count.items():
        if cnt == max_count:
            max_chars[char] = cnt
    return max_chars

