def subset_sum(numbers, target):
    result = []

    def helper(start=1, buffer=[], bufferSum=0):
        if bufferSum == target:
            result.append(list(buffer))
        if bufferSum >= target or start == len(numbers):
            return
        for i in range(start, len(numbers)):
            num = numbers[i]
            buffer.append(num)
            helper(i + 1, buffer, bufferSum + num)
            buffer.pop()
    helper()
    return result

