def analyze_sequence(text, q, ignore_case=True):
    if ignore_case:
        text = text.lower()
        q = q.lower()
    first_appearance = text.find(q)
    if first_appearance == -1:
        return (first_appearance, 0, -1)
    last_appearance = text.rfind(q)
    count = text.count(q)
    return (first_appearance, count, last_appearance)

