from typing import List

def below_zero(operations: List[int], case_insensitive: bool=False) -> bool:
    """"""
    balance = 0
    for op in operations:
        balance += op
        if case_insensitive and balance <= 0:
            return True
        elif not case_insensitive and balance < 0:
            return True
    return False
