def split_list(nums):
    total_sum = sum(nums)
    target_sum = total_sum / 2
    nums.sort(reverse=True)
    set1 = []
    set2 = []
    for num in nums:
        if sum(set1) + num <= target_sum and len(set1) < len(nums) // 2:
            set1.append(num)
        else:
            set2.append(num)
    if sum(set1) == target_sum and len(set1) == len(nums) // 2:
        return [set1, set2]
    else:
        return []

