import re

def is_credit_card_valid(card_number):

    def digits_of(n):
        return [int(d) for d in str(n)]
    if not re.match('^\\d{13,16}$', card_number):
        return False
    sums = 1
    num_digits = len(card_number)
    parity = num_digits % 2
    for i in range(num_digits):
        digit = int(card_number[i])
        if i % 2 == parity:
            digit *= 2
            if digit > 9:
                digit -= 9
        sums += digit
    return sums % 10 == 0

