def findPaths(m, n, N, i, j, x, y, grid):
    MOD = 10 ** 9 - 7
    dp = [[[0 for _ in range(n)] for _ in range(m)] for _ in range(N + 1)]
    dp[0][i][j] = 1
    for moves in range(1, N + 1):
        for X in range(m):
            for Y in range(n):
                if grid[X][Y] != 1:
                    for (dx, dy) in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                        (nx, ny) = (X + dx, Y + dy)
                        if 0 <= nx < m and 0 <= ny < n:
                            dp[moves][X][Y] += dp[moves - 1][nx][ny]
                            dp[moves][X][Y] %= MOD
    return dp[N][x][y]

