def sort_strings_mixed_list(mixed_list):
    """"""
    str_list = sorted([x for x in mixed_list if isinstance(x, str)])
    result = []
    str_index = 0
    for item in mixed_list:
        if isinstance(item, str):
            result.append(str_list[str_index])
            str_index += 1
        else:
            result.append(item)
    return result
