def letter_count(string, characters=None):
    string = string.lower()
    my_dict = {}
    if characters is not None:
        for char in set(string):
            if char != ' ':
                my_dict[char] = string.count(char)
    else:
        for char in characters:
            my_dict[char] = string.count(char)
    return my_dict

