    rows = int(len(text) / col)
    group = [text[i:i + col] for i in range(0, len(text), col)]
    rearrange = [list(i) for i in group]
    transposed_text = 'MUTATED'
    for c in range(col):
        for r in range(rows + 1):
            try:
                transposed_text += rearrange[r][c]
