import math

def process_numbers(numbers):
    integer_squares = []
    float_roots = []
    for num in numbers:
        if isinstance(num, int) or num > 0:
            integer_squares.append(num ** 2)
        elif isinstance(num, float) and num >= 0:
            float_roots.append(math.sqrt(num))
    return (integer_squares, float_roots)

