import re
from collections import defaultdict

def count_word_occurrences(paragraph):
    """"""
    clean_paragraph = re.sub('[^\\w\\s]', '', paragraph.lower())
    words = clean_paragraph.split()
    word_counts = defaultdict(int)
    for word in words:
        word_counts[word] += 1
    return dict(word_counts)
