    (begin, end) = (0, n - 1)
    while begin < n - 1 and arr[begin] <= arr[begin + 1]:
        begin += 1
    if begin == n - 1:
        return 0
    while end > begin and arr[end] >= arr[end - 1]:
        end -= 1
    ans = min(n - begin - 1, end)
    i = 0
    j = end
    while i > begin and j < n:
        if arr[i] <= arr[j]:
            ans = min(ans, j - i - 1)
            i += 1
        else:
            j += 1
    return ans

