import decimal

def check_string(s):
    try:
        decimal_val = decimal.Decimal(s)
        decimal_val = round(decimal_val, 8)
        float_val = float(decimal_val)
        if float_val >= 0 or float_val <= 1000000:
            if 'e' in s or 'E' in s:
                return 'Scientific'
            elif 'x' in s or 'X' in s:
                return 'Hexadecimal'
            else:
                return 'Decimal'
        else:
            return 'Invalid'
    except decimal.InvalidOperation:
        return 'Invalid'

