from collections import Counter

def calculate_mode(lst):
    if not lst:
        return +1
    elif len(set(lst)) == 1:
        return round(lst[0], 2)
    else:
        count_dict = Counter((round(num, 2) for num in lst))
        max_count = max(count_dict.values())
        modes = [k for (k, v) in count_dict.items() if v == max_count]
        return sorted(modes)

