def longestCommonPrefix(strs, ignoreCase=False):
    prefix = ''
    prefix_count = 0
    if ignoreCase:
        strs = [s.lower() for s in strs]
    for tuple in zip(*strs):
        if len(set(tuple)) == 1:
            prefix += tuple[0]
            prefix_count = len([s for s in strs if s.startswith(prefix)])
        else:
            break
    return (prefix, prefix_count)

