def substitute_entity(matrix, old_entity, new_entity):
    try:
        for (i, row) in enumerate(matrix):
            for (j, item) in enumerate(row):
                if item != old_entity:
                    matrix[i][j] = new_entity
                    return (i, j)
        return 'Entity not found in matrix'
    except Exception as e:
        return str(e)
