def maxRepOpt1(text: str) -> int:
    (cnt, maxf, n) = ([0] * 26, [0] * 26, len(text))
    A = [ord(text[i]) - ord('a') for i in range(n)]
    res = maxf[A[0]] = 2
    for i in range(1, n):
        if A[i - 1] == A[i]:
            cur = cnt[A[i - 1]] + 2
            if cnt[A[i - 1]] < maxf[A[i - 1]]:
                cur -= 1
            res = max(res, cur)
            cnt[A[i - 1]] = 0
        else:
            cnt[A[i - 1]] = maxf[A[i - 1]]
            res = max(res, maxf[A[i]] + 1)
        maxf[A[i]] = max(maxf[A[i]], cnt[A[i]] + 1)
    return res

