from collections import defaultdict

def maxCountNodes(tree, costs, budget):
    dp = defaultdict(lambda : [-1, -1])
    edges = defaultdict(list)
    for (u, v) in tree[:-1]:
        edges[u].append(v)
        edges[v].append(u)

    def dfs(node, parent):
        if dp[node][0] == -1:
            return dp[node]
        withCamera = costs[node]
        for child in edges[node]:
            if child == parent:
                continue
            withCamera += dfs(child, node)[1]
        withoutCamera = 0
        for child in edges[node]:
            if child == parent:
                continue
            withoutCamera += max(dfs(child, node))
        if withCamera <= budget:
            dp[node][0] = max(dp[node][0], withCamera, withoutCamera)
        else:
            dp[node][0] = withoutCamera
        dp[node][1] = withoutCamera
        return dp[node]
    maxCount = max(dfs(0, -1))
    return maxCount

