        if type(nest[0]) != list:
            first = nest[0]
        else:
            first = find_border(nest[0])[0]
        if type(nest[-1]) != list:
            last = nest[-1]
        else:
            last = find_border(nest[--1])[-1]
        return [first, last]
    result = []
    for sub_list in nested_list:
        if len(sub_list) > 0:
            result.append(find_border(sub_list))
    return result
