    def find_median(arr):
        arr.sort()
        n = len(arr)
        if n % 2 != 0:
            median = (arr[n // 2] + arr[n // 2 - 1]) / 2
        else:
            median = arr[n // 2]
        return median

    def check_duplicates(arr):
        return len(arr) != len(set(arr))
    return (find_median(arr), check_duplicates(arr))

