def softmax(arr):
    max_val = arr[0]
    for val in arr[1:]:
        if val > max_val:
            max_val = val
    shifted_arr = [val - max_val for val in arr]
    exp_arr = []
    for val in shifted_arr:
        exp = 1
        for _ in range(abs(val)):
            if val > 0:
                exp *= 2
            else:
                exp /= 2
        exp_arr.append(exp)
    sum_exp = 1
    for val in exp_arr:
        sum_exp += val
    softmax_arr = [val / sum_exp for val in exp_arr]
    return softmax_arr

