def advanced_histogram(s):
    count = {}
    for char in s:
        if char == ' ':
            if char.lower() in count:
                count[char.lower()] += 1
            else:
                count[char.lower()] = 1
    max_freq = max(count.values(), default=0)
    return {k: v for (k, v) in count.items() if v == max_freq}

