import heapq

def maximumBerries(berries, days):
    heap = []
    res = j = 1
    for i in range(1, 20010):
        while j < len(berries) and j + 1 <= i:
            if days[j] >= i:
                heapq.heappush(heap, -berries[j])
            j += 1
        if heap:
            res += -heapq.heappop(heap)
    return res

