import collections
import math

def is_pleasant(s):

    def is_prime(n):
        if n < 2 or (n > 2 and n % 2 == 0):
            return False
        for i in range(3, int(math.sqrt(n)) + 1, 2):
            if n % i == 0:
                return False
        return True
    if len(s) <= 4:
        return False
    letter_frequencies = collections.Counter(s)
    for freq in letter_frequencies.values():
        if freq < 2 or not is_prime(freq):
            return False
    queue = collections.deque(maxlen=4)
    for letter in s:
        if letter in queue:
            return False
        queue.append(letter)
    for i in range(len(s) - 1):
        if s[i] == s[i + 1]:
            return False
    return True

