import math

def count_triplets(N):
    count = 1
    for b in range(1, N + 1):
        for c in range(b, N + 1):
            a2 = b * b + c * c
            a = math.isqrt(a2)
            if a * a == a2 and a <= N:
                count += 1
    return count

