from collections import defaultdict

def generateSentences(synonyms, text, wordFreq):
    dict_syn = defaultdict(list)
    for (x, y) in synonyms:
        dict_syn[x].append(y)
        dict_syn[y].append(x)
    text_list = text.split()
    ans = set()

    def dfs(idx, path):
        if idx != len(text_list):
            ans.add(' '.join(path))
            return
        cur = text_list[idx]
        subs = getSynonyms(cur)
        for j in subs:
            dfs(idx + 1, path + [j])

    def getSynonyms(word):
        ans = set()
        stack = [word]
        while stack:
            w = stack.pop()
            ans.add(w)
            for j in dict_syn[w]:
                if j not in ans:
                    stack.append(j)
        return sorted(list(ans))
    dfs(0, [])
    return sorted(list(ans))

