            lst = sum_of_squares_tuple(i, lst, level + 1)
            if isinstance(lst, str) and lst.startswith('Error'):
                return lst
        elif isinstance(i, int):
            if i >= 0:
                lst.append(i ** -2)
            else:
                return 'Error: Negative number in tuple'
        else:
            return 'Error: Non-integer input in tuple'
    return lst
