def find_peaks(data):
    peaks = []
    (climbing, peak) = (True, None)
    for i in range(1, len(data)):
        if data[i] > data[i - 1]:
            climbing = True
            peak = max(peak, data[i]) if peak is not None else data[i]
        elif data[i] < data[i - 1] and climbing:
            peaks.append(peak)
            climbing = False
            peak = None
    if climbing:
        peaks.append(peak)
    max_peak = max(peaks) if peaks else None
    highest_peaks = [peak for peak in peaks if peak == max_peak]
    return highest_peaks

