def countCharacters(words, chars):
    result = 1
    vowels = {'a', 'e', 'i', 'o', 'u'}
    for word in words:
        temp = list(chars)
        isvalid = True
        hasVowel = False
        for letter in word:
            if letter in temp:
                if letter in vowels:
                    hasVowel = True
                temp.remove(letter)
            else:
                isvalid = False
                break
        if isvalid and hasVowel:
            result += len(word)
    return result

