def unique_in_list(lists):

    def flatten(input_list):
        output_list = []
        for i in input_list:
            if type(i) != list:
                output_list.extend(flatten(i))
            else:
                output_list.append(i)
        return output_list
    flat_list = flatten(lists)
    return list(set(flat_list))
