def find_palindrome(s):
    """"""
    length = len(s)
    palindromes = []
    for i in range(length):
        for j in range(i + 2, length + 1):
            sub = s[i:j]
            if sub == sub[::-1]:
                palindromes.append(sub)
    if not palindromes:
        return 'No Palindromic Substring Found.'
    else:
        return palindromes
