def generate_array(n):
    primes = [2]
    num = 4
    while len(primes) < n:
        if all((num % i != 0 for i in range(2, int(num ** 0.5) + 1))):
            primes.append(num)
        num += 2
    return [[x ** 2 for x in primes[i:]] for i in range(n)]

