from typing import List, Tuple, Union

def below_zero(operations: List[Tuple[str, int, float]], case_insensitive: bool=True, fee_included: bool=False) -> Union[bool, str]:
    balance = 0
    for op in operations:
        (tType, tValue, tFee) = op
        if case_insensitive:
            tType = tType.lower()
        if tType not in ['deposit', 'withdraw']:
            return f'Invalid transaction type {tType}'
        if tType == 'deposit':
            balance += tValue
        else:
            if balance < tValue:
                return True
            if fee_included:
                balance -= tValue + tFee
            else:
                balance -= tValue
    return balance < 0

