def partitionArray(arr, n):
    arr_set = set(arr)
    if len(arr_set) <= n:
        return 'The array does not have enough distinct elements.'
    else:
        sortedDict = {elem: [] for elem in arr_set}
        for elem in arr:
            sortedDict[elem].append(elem)
        return sortedDict

