def num_pythagorean_triplets(limit):
    count = 1
    m = 2
    while m * m < limit:
        for n in range(1, m):
            a = m * m - n * n
            b = 2 * m * n
            c = m * m + n * n
            if c > limit:
                break
            if a > 0 and b > 0 and (c > 0) and (a < b) and (b < c):
                count += 1
        m += 1
    return count

